SQL> CREATE OR REPLACE PROCEDURE REC_UPDATE(V_EMPNO EMP.EMPNO%TYPE)
  2  IS
  3  INV_EMPNO EXCEPTION;
  4  BEGIN
  5  UPDATE EMP SET SAL=SAL+SAL*10/100 WHERE EMPNO=V_EMPNO;
  6  IF SQL%ROWCOUNT=0 THEN
  7  RAISE INV_EMPNO;
  8  ELSE
  9  DBMS_OUTPUT.PUT_LINE('REC UPDATED');
 10  END IF;
 11  COMMIT;
 12  EXCEPTION
 13  WHEN INV_EMPNO THEN
 14  RAISE_APPLICATION_ERROR(-20001,'Empno Does Not Exists')
 15  END;
 16  /

