SQL> CREATE OR REPLACE PROCEDURE RET_ENAME(V_EMPNO  EMP.EMPNO%TYPE,
  2  V_ENAME OUT EMP.ENAME%TYPE)
  3  IS
  4  BEGIN
  5  SELECT ENAME INTO V_ENAME FROM EMP WHERE EMPNO=V_EMPNO;
  6  EXCEPTION
  7  WHEN NO_DATA_FOUND THEN
  8  RAISE_APPLICATION_ERROR(-20001,'Empno Does Not Exists');
  9  END;
 10  /

